@import url('https://fonts.googleapis.com/css2?family=Roboto&family=Roboto+Slab:wght@700&display=swap');

:root{
	--darkblue: #1D3557;
	--blue: #457B9D;
	--lightblue:#A8DADC ;
	--white: #F1FAEE;
	--red: #E63946;
}

*{
	margin:0;
	padding: 0;
	box-sizing: border-box;
	font-family: 'Roboto' , sans-serif;
}

p{
	line-height: 1.5;
	margin-top: 10px;
}

main{
	margin: auto;
	max-width: 1920px;
}

.flex{
	display: flex;
	flex-flow: row wrap;
	justify-content: space-between;
}

.link{
	color: var(--white);
	text-decoration: none;
}

/*HEADER*/

.header{
	align-items: center;
	background-color: var(--darkblue);
	padding: 20px;
	color: var(--white);
}

.logo__link{
	text-decoration: none;
}

.logo__image{
	height: 50px;
	width: 50px;
}

.logo__title{
	display: none;
}

.menu-nav__button{
	display: inline;
	padding-left: 10px;
	font-size: 0.9rem;
}

.menu-nav__button a{
	transition: color 1s;
}

.menu-nav__button a:hover{
	color: var(--red)
}

.header__button a{
	background-color: var(--red);
	padding: 5px 12px;
	border-radius: 6px;
	text-decoration: none;
	color: var(--white);
	font-size: 0.8rem;
	transition: background-color 0.8s ease-out;
}

.header__button a:hover{
		background-color: var(--white);
		color: var(--red);
	}


/*FOOTER*/

.footer{
	align-items: center;
	background-color: var(--darkblue);
	padding: 20px;
	color: var(--white);
	border: 1px solid green;
	font-size: 0.8rem;
}

.footer-list{
	list-style-type: none;
	margin-top: 10px;
}

.footer-list__link{
	display: inline;
	padding-left: 5px;
}

.imgpath{
	clip-path: polygon(0 0, 0 20%, 0 40%, 10% 60%, 5% 90%, 
		50% 100%, 95% 90%, 90% 60%, 100% 40%, 100% 20%, 100% 0, 
		90% 20%, 80% 0, 70% 20%, 60% 0, 50% 20%, 40% 0, 30% 20%, 20% 0, 10% 20%);
	width: 50px;
	height: 50px;
}

.footer-title p{
	display: inline;
	font-size: 1rem;

}

/*MAIN*/

.main__cover{
	width: 100%;
	max-height: 300px;
	margin:0 auto;
	display: block;
}

.cover__detail-img{
	display: block;
	max-width: 100%;
	margin: 0 auto;
}

/*.section {
	margin: 50px 20px;
}*/

.section__title-h1{
	font-family: 'Roboto Slab', serif;
	font-size: 2rem;
	color: var(--blue);
	margin-bottom: 10px;
	border-bottom: 1px solid var(--blue);
	text-align: left;
}

.section__title-h2{
	margin-bottom: 10px;
	border-bottom: 1px solid var(--blue);
	color: var(--blue);
	font-size: 1.6rem;
	font-family: 'Roboto Slab' , serif;
}


/* HOME */

/* bio */
.section-biography__img{
	width: 100%;
	margin: 30px 0px;
}

/* noveles */

.novels__gallery{
	background-color: var(--darkblue);
	color: var(--white);
	font-size: 1.5rem;
	text-align: center;
	margin: 30px 0px;
	cursor: pointer;
}

.novels__gallery-img{
	width: 100%;
}

.novels__gallery-title{
	padding: 5px;
}

.novels__gallery-subtitle{
	font-size: 0.8rem;
}

/* premis */
.section-prize__list{
	margin: 20px 0px;
	list-style: none;
	background-color: var(--lightblue);
	color: var(--blue);
}

.section-prize__list-element{
	border: 1px solid var(--blue);
	padding: 10px;
}

.section-prize__logos{
	display: none;
}

/* TERROR */

.cover-genre__terror{
	background-image: url(images/terror.jpg);
	background-repeat: no-repeat;
	background-position: center top;
	background-size: cover;

}

.cover-genre__thriller{
	background-image: url(images/sobrenatural.jpg);
	background-repeat: no-repeat;
	background-position: center top;
	background-size: cover;

}


.cover-genre__title{
	padding: 100px 0px;
	text-align: center;
	color: var(--white);
	font-size: 3rem;
	font-family: 'Roboto slap' , serif;
}


.breadcrumb__list{
	margin: 20px;
	font-size: 0.8rem;
}

.breadcrumb__list-element{
	display: inline;
	list-style-type: none;
}

.breadcrumb__list-element--active{
	text-decoration: underline;
}

.breadcrumb__list-element--link{
	text-decoration: none;
	color: initial;
}

.novel-article{
	margin: 50px 0px;
}

.novel-article img{
		width: 100%;
}

.novel-article__title{
	margin-bottom: 10px;
	text-decoration: underline;
	font-size: 1.5rem;
}

.novel-article__link{
	background-color: var(--red);
	padding: 5px 10px;
	text-decoration: none;
	border-radius: 5px;
	color: var(--white);
}

/* DETALL */

.cover__detail-img{
	display: block;
	max-width: 100%;
	margin: 0 auto;
}

.section-intro__figure{
	margin: 30px 0px;
	text-align: center;	
	width: 100%;
}
.section-intro__figure-img{
	width: 50%;	
}

.section-detail__video{
	width: 100%;
	margin: 20px auto;
	border: none;
	max-width: 500px;
	min-height: 250px;
	max-height: 250px;
}

.section-multimedia img{
	width: 100%;
}

.section-more-books__list{
	text-align: center;
	margin: 20px 0px;
}

.section-more-books__list-element{
	display: inline;
	list-style-type: none;
	margin-right: 10px;
}

.section-more-books__list-element--link{
	text-decoration: none;
	color: var(--black);
}

@media only screen and (min-width: 768px){

	/*general*/
	.header{
		font-size: 1.2rem;
		padding: 1% 5%;
	}

	.logo{
		width: 20%;	
	}

	.logo__image{
		display: block;
	}

	.menu{
		width: 60%;
		text-align: center;
	}

	.header__button{
		width: 20%;
		text-align: right;
	}

	.menu-nav__button{
		padding: 0 30px;
	}

	/*.section{
		padding: 0px 50px;
		margin: 50px 0px;
	}*/
	
	.section__title-h1{
		font-size: 3rem;
	}

	.section__text{
		text-align:center;
		margin: 1% 20%;
	}

	.footer{
		padding: 1% 5%;
		height: 80px;
	}

	/*HOME*/

	.logo__title{
		display: block;
	}

	.main__cover{
		max-height: 340px;
		width: 100%;
	}

	.title__home{
		animation: bounceIn ease 2.5s;
	}

	.par__home{
		animation: fadeIn ease 3s;
	}

	@keyframes bounceIn{
		0%{
			transform: scale(0.5);
		}

		40%{
			transform: scale(1.3);
		}

		100%{
			transform: scale(1);
		}
	}

	@keyframes fadeIn{
		0%{
			opacity: 0;
		}
		100%{
			opacity: 1;
		}
	}

	.novels__gallery{
		width: 30%;
		margin: 20px 0;
		transition: 1s width;
	}

	.novels__gallery:hover{
		width: 28%;
	}

	.section-prize__list{
		display: none;
	}
	
	.section-prize__logos{
		display: flex;
		margin-top: 20px;
	}
	
	.section-prize__logos-item {
		text-align: center;
		font-size: 0.8rem;
	}

	.fas{
		color: var(--red);
		padding-bottom: 10px;
	}

	/*CATEGORY*/

	.cover-genre__terror{
		background-image: url(images/portadaTerror.jpg);
		background-repeat: no-repeat;
		background-position: center top;
		background-size: cover;

	}

	.cover-genre__thriller{
		background-image: url(images/portadaThriller.jpg);
		background-repeat: no-repeat;
		background-position: center top;
		background-size: cover;
	}

	.breadcrumb__list{
		margin: 1% 5%;	
	}

	.section-novels{
		margin: 0px;
	}

	.section-novels-list{
		display: flex;
		justify-content: flex-start;
	}

	.novel-article{
		width: 30%;
		padding-right: 50px;
		padding-bottom: 30px;
		margin: 20px 0;

	}

	.novel-article img{
		width: 100%;
	}

	/* DETAIL */

	.section-intro-detail{
		align-items: center;
	}

	.section-intro__text{
			width: 50%;
	}

	.section-intro__figure{
		width: 50%;
		padding-left: 50px;

	}

	.section-intro__figure-img{
		width: 200px;
	}

	.section-detail__video{
		width: 100%;
		margin: 20px auto;
		max-width: 770px;
		min-height: 400px;
		border: none;
	}
}

@media only screen and (min-width: 1024px) {

	/*HOME*/

	/*biografia*/

	.section-biography__intro{
		width: 50%;
		padding-right: 50px;
	}

	.section-biography__img{
		width: 50%;
		padding-left: 50px;
	}

	.section-multimedia{
		align-items: center;
		justify-content: space-around;
	}

	.section-detail__video{
		width: 60%;
		margin: 20px 0;
		padding-right: 20px;
		border: none;
		min-height: 260px;
		max-width: 500px;
		
	}

	.section-multimedia img{
		width: 40%;
		max-width: 450px;
	}
	
}
